#import "ViewController.h"
#import "AdsforceSDK.h"

@interface ViewController ()

@end

@implementation ViewController

- (void)viewDidLoad {
    [super viewDidLoad];
    self.view.backgroundColor = [UIColor whiteColor];
    
    UIButton *button1 = [UIButton buttonWithType:UIButtonTypeRoundedRect];
    button1.backgroundColor = [UIColor colorWithRed:254/255.0 green:208/255.0 blue:48/255.0 alpha:1];
    button1.layer.cornerRadius = 10;
    button1.frame = CGRectMake(self.view.frame.size.width/2 - 250/2, 100, 250, 40);
    [button1 setTitle:@"GetDeeplink" forState:UIControlStateNormal];
    [button1 addTarget:self action:@selector(getDeeplinkClick) forControlEvents:UIControlEventTouchUpInside];
    [self.view addSubview:button1];
    
    UIButton *button2 = [UIButton buttonWithType:UIButtonTypeRoundedRect];
    button2.backgroundColor = [UIColor colorWithRed:254/255.0 green:208/255.0 blue:48/255.0 alpha:1];
    button2.layer.cornerRadius = 10;
    button2.frame = CGRectMake(self.view.frame.size.width/2 - 250/2, 170, 250, 40);
    [button2 setTitle:@"IAP" forState:UIControlStateNormal];
    [button2 addTarget:self action:@selector(iapClick) forControlEvents:UIControlEventTouchUpInside];
    [self.view addSubview:button2];
    
    UIButton *button3 = [UIButton buttonWithType:UIButtonTypeRoundedRect];
    button3.backgroundColor = [UIColor colorWithRed:254/255.0 green:208/255.0 blue:48/255.0 alpha:1];
    button3.layer.cornerRadius = 10;
    button3.frame = CGRectMake(self.view.frame.size.width/2 - 250/2, 240, 250, 40);
    [button3 setTitle:@"ThirdPay" forState:UIControlStateNormal];
    [button3 addTarget:self action:@selector(thirdPayClick) forControlEvents:UIControlEventTouchUpInside];
    [self.view addSubview:button3];
    
    UIButton *button4 = [UIButton buttonWithType:UIButtonTypeRoundedRect];
    button4.backgroundColor = [UIColor colorWithRed:254/255.0 green:208/255.0 blue:48/255.0 alpha:1];
    button4.layer.cornerRadius = 10;
    button4.frame = CGRectMake(self.view.frame.size.width/2 - 250/2, 310, 250, 40);
    [button4 setTitle:@"CustomEvent" forState:UIControlStateNormal];
    [button4 addTarget:self action:@selector(customEventClick) forControlEvents:UIControlEventTouchUpInside];
    [self.view addSubview:button4];
}

- (void)getDeeplinkClick {
    [AdsforceSDK getDeeplink:^(AdsforceDeeplinkModel *deeplinkModel) {
        NSLog(@"%@",deeplinkModel);
    }];
}

- (void)iapClick {
    NSString *receiptDataString = @"";
    NSNumber *productPrice = [NSNumber numberWithDouble:6.00];
    NSString *productCurrencyCode = @"CNY";
    NSString *pubkey = @"1111";
    
    [AdsforceSDK appStoreWithProductPrice:productPrice
                    productCurrencyCode:productCurrencyCode
                      receiptDataString:receiptDataString
                                 pubkey:pubkey
                                 params:nil];
}

- (void)thirdPayClick {
    NSNumber *productPrice = [NSNumber numberWithDouble:6.00];
    NSString *productCurrencyCode = @"CNY";
    NSString *productIdentifier = @"1111";
    NSString *productCategory = @"1111";
    
    [AdsforceSDK thirdPayWithProductPrice:productPrice
                    productCurrencyCode:productCurrencyCode
                      productIdentifier:productIdentifier
                        productCategory:productCategory];
}

- (void)customEventClick {
    [AdsforceSDK customEventWithKey:@"testStrKey" stringValue:@"testStrValue"];
    
    NSArray *arr = [[NSArray alloc] initWithObjects:
                    @"testArrayValue1",
                    @"testArrayValue2",
                    @"testArrayValue3", nil];
    [AdsforceSDK customEventWithKey:@"testArrayKey" arrayValue:arr];
    
    NSDictionary *dic = [[NSDictionary alloc] initWithObjectsAndKeys:
                         @"testMapKey1",@"testMapValue1",
                         @"testMapKey2",@"testMapValue2",
                         @"testMapKey3",@"testMapValue3",nil];
    [AdsforceSDK customEventWithKey:@"testMapKey" dictionaryValue:dic];
}

@end
